import React,{useEffect} from 'react';
import {gql,useQuery} from '@apollo/client';

import { List,
        ListItem,
        ListIcon,
        OrderedList,
        UnorderedList } from "@chakra-ui/react";

const GET_BOOKS=gql`
    query GetBooks{
        books{
            name
            id
        }
    }
`

export const BookList=(GET_BOOKS)=>{
    const { loading, error, data } = useQuery(GET_BOOKS);

    if (loading) return 'Loading...';
    if (error) return `Error! ${error.message}`;

    // useEffect(()=>{
    //     console.log(data)
    // },[data])

    return(
        <div>
            <UnorderedList>
                <ListItem>
                    Book Nmae
                    {data.books.map(books => (
                    <ListItem key={books.id} value={books.nae}>
                    {books.name}
                    </ListItem>
                ))}
                </ListItem>
            </UnorderedList>
        </div>
    );
}
