import React,{useEffect,useState} from 'react';
import {useMutation, useQuery} from '@apollo/client';
import { GET_BOOKS,DELETE_BOOK} from '../queries/queries';
import { BookDetails } from './BookDetails';
import {
        Grid,
        GridItem,
        Button,
        ButtonGroup,
        Box,
        Wrap,
        useDisclosure,
        IconButton } from "@chakra-ui/react";
import { DeleteIcon} from '@chakra-ui/icons'

// const gql=require('graphql-tag');



export const BookList=()=>{
    const [selected,setSelected]=useState(null);
    const { isOpen, onToggle } = useDisclosure();
    const { loading, error, data } = useQuery(GET_BOOKS);
    const [deleteBook]=useMutation(DELETE_BOOK);
    if (loading) return 'Loading...';
    if (error) return `Error! ${error.message}`;

    handleDelete=()=>{
        e.preventDefault();
        deleteBook({variables:{id:IconButton.value},
                    refetchQueries:[{query:GET_BOOKS}]});
    }

    return(
        <div>
            <Grid minH={'xs'}
                  templateColumns="repeat(2,1fr)"
                  gap={4}>
                <GridItem colSpan={1}>
                    <Wrap spacing={4}>
                    {data.books.map(dog => (
                        <ButtonGroup isAttached>
                        <Button 

                        onClick={(e)=>{
                                    setSelected(selected=>dog.id);
                                    }} 
                        key={dog.id} value={dog.name}>
                        {dog.name}
                        </Button>
                                    <IconButton key={dog.id} value={dog.id} onClick={handleDelete} aria-label="Delete" icon={<DeleteIcon/>}/>
                        </ButtonGroup>
                    ))}
                    </Wrap>
                </GridItem>

                    <GridItem colSpan={1}>
                            <BookDetails bookid={selected}/>
                    </GridItem>

            </Grid>
        </div>
    );
}
