import React,{useEffect,useState} from 'react';
import {useQuery} from '@apollo/client';
// import { Select } from "@chakra-ui/react";
import { Stack,
    InputGroup,
    InputLeftAddon,
    Select,
    Input,
    Button } from "@chakra-ui/react";
import { GET_AUTHORS } from '../queries/queries';


export const AddBook=()=>{
    const [book,setBook]=useState("");
    const [genre,setGenre]=useState("");
    const [author,setAuthor]=useState("");
    const handleSubmit=(e)=>{
        e.preventDefault();
        console.log(book,genre); 
    }
    const { loading, error, data } = useQuery(GET_AUTHORS);

    if (loading) return 'Loading...';
    if (error) return `Error! ${error.message}`;

    // useEffect(()=>{
    //     console.log(data)
    // },[data])
    // console.log(data)
    return(
        <div>
            <Stack spacing={4}>
                <h2>{book}</h2>
              <InputGroup>
                    <InputLeftAddon children="Book" />
                    <Input value={book} required onChange={(e)=>setBook(e.target.value)}/>
                </InputGroup>
                <h2>{genre}</h2>
              <InputGroup>
                    <InputLeftAddon children="Genre" />
                    <Input value={genre} required onChange={(e)=>setGenre(e.target.value)}/>
                </InputGroup>

            <Select placeholder="Select author" value={author} onChange={(e)=>setAuthor(e.target.value)}>
            {data.authors.map(dog => (
                <option key={dog.id} value={dog.name}>
                {dog.name}
                </option>
            ))}
            </Select>

                <Button type="submit" onClick={handleSubmit} colorScheme="blue">Add</Button>

            </Stack>
        </div>
    );
}
